import Navbar from "./components/Navbar"
import Show from "./pages/Show"
import Home from "./pages/Home"
import Actor from "./pages/Actor"
import { Route, Routes } from "react-router-dom"
import { useState, useEffect } from "react";


function App() {
	const [entry, setEntry]   = useState("");
	const [myList, setMyList] = useState([]);
  const [user, setUser]     = useState("");
	

	useEffect(() => {
		setDBList();
}, [myList])


const getMALData = async() => {
    console.log("getting MAL data")
    const malData = await fetch ('/mal', {
      method: 'POST',
      headers: {
        'content-type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify({
        Username: entry
      })
    })
    .then(res => res.json());
    let temp = [];
    for (let i in malData.data) {
        temp[i] = malData.data[i].node.id;
    }
    console.log(temp)
    setMyList(temp)
    if (temp.length > 0)
      setUser(entry)
}


const setDBList = async() => {
    const lister = await fetch('/list', {
        method: 'POST',
        headers: {
          'content-type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          ids: myList
        })
      })
}

  return (
	<>
      <div className="userSearchArea">
	  	{/* <h6>{user}</h6> */}
            <input id="userSearch"
				type="text"
                placeholder="MAL Username" 
                value={entry} 
                onChange={(e) => setEntry(e.target.value)}></input>
            <button id="userSearchButton" onClick={() => getMALData()}>Filter by User</button>
      </div>
        <Navbar username={user}/>
        <div className="container">
          <Routes>
            <Route path="/" element={<Home user={user}/>} />
            <Route path="/Show/:id?/:title?" element={<Show user={user}/>} />
            <Route path="/Actor/:id?" element={<Actor user={user}/>} />
          </Routes>
        </div>
        {/* <button onClick={getData}>Click</button> */}
	</>
  
  );
}
export default App

  
  
  





