import Navbar from "./components/Navbar"
import Show from "./pages/Show"
import Home from "./pages/Home"
import Actor from "./pages/Actor"
import { Route, Routes } from "react-router-dom"
import { useState, useEffect } from "react";


function App() {
	const [user, setUser] = useState("")
	const [myList, setMyList]   = useState([]);
	

	useEffect(() => {
		setDBList();
}, [myList])


const getMALData = async() => {
    console.log("getting MAL data")
    const malData = await fetch ('/mal', {
      method: 'POST',
      headers: {
        'content-type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify({
        Username: user
      })
    })
    .then(res => res.json());
    let temp = [];
    for (let i in malData.data) {
        temp[i] = malData.data[i].node.id;
    }
    setMyList(temp)
}


const setDBList = async() => {
    const lister = await fetch('/list', {
        method: 'POST',
        headers: {
          'content-type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          ids: myList
        })
      })
}

  return (
      <>
	  	<h6>{user}</h6>
            <input type="text"
                placeholder="MAL Username" 
                value={user} 
                onChange={(e) => setUser(e.target.value)}></input>
            <button onClick={() => getMALData({user})}>api test</button>
        <Navbar username={user}/>
        <div className="container">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/Show/:id?/:title?" element={<Show />} />
            <Route path="/Actor/:id?" element={<Actor />} />
          </Routes>
        </div>
        {/* <button onClick={getData}>Click</button> */}
      </>
  
  );
}
export default App

  
  
  





