import * as React from 'react';
import { Link, useMatch, useResolvedPath } from "react-router-dom"
import {useState, useEffect, useContext} from "react";

const Navbar = ({ username }) => {
    var list = "All Anime";
    const [user, setUser] = useState("");
    const [myList, setMyList]   = useState([]);


    useEffect(() => {
        setDBList();
    }, [myList])


    const getMALData = async() => {
        console.log("getting MAL data")
        const malData = await fetch ('/mal', {
          method: 'POST',
          headers: {
            'content-type': 'application/json',
            'Accept': 'application/json'
          },
          body: JSON.stringify({
            Username: user
          })
        })
        .then(res => res.json());
        let temp = [];
        for (let i in malData.data) {
            temp[i] = malData.data[i].node.id;
        }
        setMyList(temp)
    }

    
    const setDBList = async() => {
        const lister = await fetch('/list', {
            method: 'POST',
            headers: {
              'content-type': 'application/json',
              'Accept': 'application/json'
            },
            body: JSON.stringify({
              ids: myList
            })
          })
    }


    return (
        <nav className="nav">
            <div id='navLeftPane'>
                <Link to="/" className="site-title">
                    Home
                </Link>
                <h6 id='filter'>Filtered by {username.length > 0 ? username : "All Anime"}</h6>
            </div>
            <ul>
                {/* <h6>{user}</h6>
                <input type="text"
                    placeholder="MAL Username" 
                    value={user} 
                    onChange={(e) => setUser(e.target.value)}></input>
                <button onClick={() => getMALData({user})}>api test</button> */}
                <CustomLink to="/Show/">Show Search</CustomLink>
                <CustomLink to="/Actor/">Actor Search</CustomLink>
                {/* <h3>Sign in to Filter List</h3> */}
                {/* <input placeholder='Sign in to Filter'></input> */}
            </ul>
        </nav>
    )
    
    module.exports = {
        user,
        myList
    }
}
      
    function CustomLink({ to, children, ...props }) {
        const resolvedPath = useResolvedPath(to)
        const isActive = useMatch({ path: resolvedPath.pathname, end: true })
        
        return (
            <li className={isActive ? "active" : ""}>
            <Link to={to} {...props}>
                {children}
            </Link>
            </li>
        )
    }



