import Navbar from "./components/Navbar";
import Show from "./pages/Show";
import Home from "./pages/Home";
import Actor from "./pages/Actor";
import { Link, useMatch, useResolvedPath } from "react-router-dom"
import { Route, Routes, useLocation } from "react-router-dom";
import { useCookies } from "react-cookie";
import { useState, useEffect } from "react";
import OAuth from "./pages/OAuth";


function App() {
	const [cookies, setCookies] = useCookies(["acc", "list"])
	const [entry, setEntry]  	= useState(cookies.acc || "");
	const [myList, setMyList] 	= useState(cookies.list || []);
  	const [user, setUser]     	= useState(cookies.acc || "");
	
	let location = useLocation();
	

	// useEffect(() => {
	// 	setDBList();
	// }, [myList])

	// useEffect(() => {
	// 	getMALData()
	// }, [])


	const getMALData = async() => {
		if (entry != "") {
			console.log("getting MAL data!!!!!!")
			try {
				const malData = await fetch ('/api/mal', {
					method: 'POST',
					headers: {
						'content-type': 'application/json',
						'Accept': 'application/json'
					},
					body: JSON.stringify({
						Username: entry
					})
				})
				.then(res => res.json());

				console.log(typeof(malData));
				if (!malData["data"]) {
					// console.log(malData)
					let authURL = "http://myanimelist.net/v1/oauth2/authorize?response_type=code&client_id=5dbcd29b3178e6d62ec7ecf17b4daf56&code_challenge=hqzHDMTFAf4LXM0e98VK3JDwI-ajZRkzv2fclWeDH-Sw3dIH-rNSbh6yVncSzcWn4IvVfkO-JKnLVT9MX6x5Doj7GDqhtU3B3IMEaDa3UNYwziF0L6vZ2F-G0v8aFuVh\r\n\r\n"
					//malData["url"];
					const authPopup = window.open(authURL, "", `left=${window.screenLeft},top=${window.screenTop},width=600,height=800`);
					authPopup.window.addEventListener('click', () => console.log(authPopup.window.location))
					// alert("Your List is marked as private. Please make it public to use this feature.")
				}
				else {

				// instead of array we're turning the list into a comma separated string
					// let temp = [];
					// for (let i in malData.data) {
					// 	temp[i] = malData.data[i].node.id;
					// }
					let str = "("
					for (let i in malData.data) {
						str += malData.data[i].node.id + ","
					}
					str = str.slice(0, str.length - 1) + ")"
					// console.log(str)
					setMyList(str)
					if (str.length > 0) {
						setUser(entry)
						setCookies('acc', entry, {path: '/'})
						setCookies('list', str, {path: '/'})
					}
				}
			} catch (error) {
				console.log(error)
			}
		}
	}


	function userFilter() {
		getMALData()
	}

	// const setDBList = async() => {
	// 	if (myList.length > 0) {
	// 		const myListString = await fetch('/api/list', {
	// 			method: 'POST',
	// 			headers: {
	// 			'content-type': 'application/json',
	// 			'Accept': 'application/json'
	// 			},
	// 			body: JSON.stringify({
	// 				ids: myList
	// 			})
	// 		})
	// 	}
	// }

	return (
		<div className="app">
			{/* {console.log(cookies)} */}
			
			{/* <Navbar username={user}>
				<h1>Hello</h1>
			
			</Navbar> */}
			<nav className="nav">
            <div id='navLeftPane'>
				{/* <CustomLink to="/" className="site-title" end="true">Home</CustomLink> */}
                <CustomLink to="/" id="homeButton" onClick={HomeClick}>Home</CustomLink> {/*className="site-title"*/}
            </div>
			<div id="userSearchArea">
					<div id="userSearchInputs">
						<input id="userSearch"
							type="text"
							placeholder="MAL Username" 
							value={entry} 
							onChange={(e) => setEntry(e.target.value)}
							onKeyDown={(e) => handleKeyDown(e)}></input>
						<button id="userSearchButton" onClick={userFilter}>Filter by User</button>
					</div>
				<div id='filterLabel'>
					<h6 id='filter'>Filtered by: {user.length > 0 ? user : "All Anime"}</h6>
					{user != ""
						? <button id='unfilter' onClick={removeFilter}>Remove Filter</button>
						: <></>
					}
				</div>
			</div>
            <ul id="navPages">
                <CustomLink to="/Anime/">Anime Search</CustomLink>
                <CustomLink to="/Actor/">Actor Search</CustomLink>
            </ul>
        	</nav>
			<Routes>
				<Route path="/Anime/:id?/:title?" element={<Show user={user} myList={myList}/>} />
				<Route path="/Actor/:id?" element={<Actor user={user} myList={myList}/>} />
				<Route path="/" element={<Home user={user} myList={myList}/>} />
				<Route path="/OAuth/:code?" element={<OAuth />} />

			</Routes>
			{/* <button onClick={getData}>Click</button> */}
		</div>
	
	);

	function CustomLink({ to, children, ...props }, end) {
        const resolvedPath = useResolvedPath(to)
        const isActive = useMatch({ path: resolvedPath.pathname, end: end })
        
        return (
            <li className={isActive ? "active" : ""}>
            <Link to={to} {...props}>
                {children}
            </Link>
            </li>
        )
    }

	function HomeClick() {
		console.log(window.location.href)
		if (window.location.pathname = '/') {
			// window.location.replace(window.location.href);
			console.log(location.pathname)
		}
	}

	function handleKeyDown(e) {
		if (e.key === 'Enter') {
			userFilter();
		}
	}

	function removeFilter() {
		setUser("")
		setMyList([])
		setEntry("")
		setCookies('acc', "", {path: '/'})
	}
}
export default App

  
  
  





